import React, { useEffect, useRef } from 'react';
import ROSLIB from 'roslib';
const MapViewer = ({ rosUrl, mapDivId = "map", width = 800, height = 600 }) => {
  const viewerRef = useRef(null);

  useEffect(() => {
    // Connect to ROS
    const ros = new ROSLIB.Ros({
      url: rosUrl,
    });

    // Create the viewer
    const viewer = new ROS2D.Viewer({
      divID: mapDivId,
      width,
      height,
    });

    // Store viewer in ref for later use
    viewerRef.current = viewer;

    // Set up the grid client
    const gridClient = new ROS2D.OccupancyGridClient({
      ros,
      rootObject: viewer.scene,
    });

    // Center the map once the grid is loaded
    gridClient.on("change", () => {
      viewer.scaleToDimensions(gridClient.currentGrid.width, gridClient.currentGrid.height);
      viewer.shift(gridClient.currentGrid.pose.position.x, gridClient.currentGrid.pose.position.y);
    });

    return () => {
      // Cleanup if needed (e.g., closing the ROS connection)
      ros.close();
    };
  }, [rosUrl, mapDivId, width, height]);

  return <div id={mapDivId} style={{ width, height }} />;
};

export default MapViewer;
